mapper.select:

     	select approval_id,mem_id,approval_type_id,approval_commit_id,approval_inter_id,approval_final_id,approval_reject,approval_content
		from (
				select /*+INDEX_DESC(approval pk_approval)*/ rownum rn, approval_id,mem_id,approval_type_id,approval_commit_id,approval_inter_id,approval_final_id,approval_reject,approval_content 
    			from (
    					select approval_id,mem_id,approval_type_id,approval_commit_id,approval_inter_id,approval_final_id,approval_reject,approval_content 
    					from approval 
    					order by TO_NUMBER(REGEXP_SUBSTR(approval_id, '[0-9]+',1,1)) desc
    		 		 )
    			where



		approval_id >= 'APPR1' and rownum <= (#{pageNum} * #{amount}))
		where rn > (#{pageNum} - 1) * #{amount}
